// üé≤ Holographic Spinning Cube
const HolographicCube = () => {
  const cubeRef = useRef();

  useFrame(() => {
    if (cubeRef.current) {
      cubeRef.current.rotation.x += 0.01;
      cubeRef.current.rotation.y += 0.02;
      cubeRef.current.position.y = Math.sin(Date.now() * 0.001) * 0.5; // Floating effect
    }
  });

  return (
    <mesh ref={cubeRef}>
      <boxGeometry args={[1.5, 1.5, 1.5]} />
      <meshStandardMaterial color="cyan" emissive="blue" emissiveIntensity={0.5} />
    </mesh>
  );
};

